import { faCalendar, faBed, faPeopleGroup} from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import React ,{useState}  from 'react'
import 'react-date-range/dist/styles.css'; // main style file
import 'react-date-range/dist/theme/default.css'; // theme css file
import { DateRange } from 'react-date-range';
import * as locales from 'react-date-range/dist/locale';
import "./header.scss"

const Header = () => {
  const [openCalendar, setOpenCalendar] = useState(false);
  const [dates, setdates] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: 'selection'
    }
  ]);
  return (
    <div className="header">
      <div className="headerContainer">
        <h1 className="headerTitle">
          尋找下趟住宿
        </h1>
        <p className="headerDes">搜尋飯店、民宿及其他住宿類型的優惠...
          <br />Booking.com 提供全球超過 1,500,000 間住宿</p>
        <div className="headerSearchBar">
          <div className="SearchBarItem">
            <FontAwesomeIcon icon={faBed} />
            <input type="Search" placeholder='你要去哪裡？' className='SearchInput' />
          </div>
          <div className="SearchBarItem">
            <FontAwesomeIcon icon={faCalendar} onClick={()=>setOpenCalendar(!openCalendar)}/>
            <span className="SearchText"  onClick={()=>setOpenCalendar(!openCalendar)}>
              08/16/2022-08/1602022</span>
              {openCalendar && <DateRange
                editableDateInputs={true}
                onChange={item => setdates([item.selection])}
                moveRangeOnFirstSelection={false}
                ranges={dates}
                locale={locales['zhCN']}
              
              />}
          </div>
          <div className="SearchBarItem">
            <FontAwesomeIcon icon={faPeopleGroup} />
            <span className="SearchText">3位成人，2位小孩，1間房</span>
          </div>
          <button className='SearchBarBtn'>搜尋</button>
        </div>
      </div>
    </div>
  )
}
export default Header
