import { faAngleLeft, faAngleRight, faHeartCircleCheck, faLocationDot, faPeopleGroup, faSmokingBan, faWifi, faXmark } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useRef, useState } from 'react'
import Footer from '../components/Footer';
import Navbar from '../components/Navbar'
import { gsap } from "gsap";
import "./hotel.scss"
import { set } from 'date-fns';
const Hotel = () => {
  const [sliderIndex, setSiderIndex] = useState(0);
  const [openSlider, setOpenSlider] = useState(false);

  let comments = useRef(null)
  const photos = [
    {
      src: "https://cf.bstatic.com/xdata/images/hotel/max1280x900/261707778.jpg?k=56ba0babbcbbfeb3d3e911728831dcbc390ed2cb16c51d88159f82bf751d04c6&o=&hp=1",
    },
    {
      src: "https://cf.bstatic.com/xdata/images/hotel/max1280x900/261707367.jpg?k=cbacfdeb8404af56a1a94812575d96f6b80f6740fd491d02c6fc3912a16d8757&o=&hp=1",
    },
    {
      src: "https://cf.bstatic.com/xdata/images/hotel/max1280x900/261708745.jpg?k=1aae4678d645c63e0d90cdae8127b15f1e3232d4739bdf387a6578dc3b14bdfd&o=&hp=1",
    },
    {
      src: "https://cf.bstatic.com/xdata/images/hotel/max1280x900/261707776.jpg?k=054bb3e27c9e58d3bb1110349eb5e6e24dacd53fbb0316b9e2519b2bf3c520ae&o=&hp=1",
    },
    {
      src: "https://cf.bstatic.com/xdata/images/hotel/max1280x900/261708693.jpg?k=ea210b4fa329fe302eab55dd9818c0571afba2abd2225ca3a36457f9afa74e94&o=&hp=1",
    },
    {
      src: "https://cf.bstatic.com/xdata/images/hotel/max1280x900/261707389.jpg?k=52156673f9eb6d5d99d3eed9386491a0465ce6f3b995f005ac71abc192dd5827&o=&hp=1",
    },
    {
      src: "https://cf.bstatic.com/xdata/images/hotel/max1024x768/231327417.jpg?k=168988bd68edf0d13697b0e9b8ae896dfefff105d54a4aec17bdbe8c3a76d5b1&o=&hp=1",
    },
    {
      src: "https://cf.bstatic.com/xdata/images/hotel/max1024x768/231327417.jpg?k=168988bd68edf0d13697b0e9b8ae896dfefff105d54a4aec17bdbe8c3a76d5b1&o=&hp=1",
    },
  ];
  const clickSlider = (index)=> {
    setOpenSlider(true);
    setSiderIndex(index);
  }
  const handleHover = e => {
    gsap.to(comments, {
      css: {
        display:"flex",
        opacity:1,
      },
      ease: "power3.inOut",
    })
  }
  //離開特效
  const handleHoverExit = e => {
    gsap.to(comments, {
      css: {
        display:"none",
        opacity:0,
      },
      ease: "power3.inOut",
    })
  }
  const slideDirection = (direction) => {
    let newSliderIndex;
    let lastPicture = photos.length - 1;
    if (direction == "left") {
      sliderIndex == 0 ? newSliderIndex = lastPicture: newSliderIndex = sliderIndex - setSiderIndex(newSliderIndex)
    } else {
      sliderIndex == lastPicture ? newSliderIndex = 0 : newSliderIndex = sliderIndex + 1;
      setSiderIndex(newSliderIndex)
    }
  }
  return (
    <div className="hotel">
      <Navbar />
      {openSlider &&
      <div className="slider">
        <div className="sliderWrapper">
          <div className="wrapperTitle">
            <div className="TitleName">台南微醺文旅</div>
            
          </div>
        </div>
      </div>
      }
    </div>
  )
}

export default Hotel
